import image from './image.JPG';
import heeya from './heeya.JPG';
import CardDetail from './cardDetail.js';
import React , { useState } from 'react';
import './App.css';

function Card(props) {
  const [cardDetailUI, onCardDetailUI] = useState(false);

    return (
      <div className="Card">
        <div  className="UserDate"><img src={ heeya } width='15' height='15'></img> heeya </div>
        <h3  onClick={ ()=>{ onCardDetailUI(true) } }> {  props.title.title } </h3>
        <p> { props.title.time } </p>
        <img src={ image } width='200' height='200' />
        <div>좋아요 ♡</div>
        <hr />
        {
          cardDetailUI === true
          ? <CardDetail />
          : null
        }
      </div>
    )
  }


export default Card
