import React , { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  let [cardDetail, onCardDetail] = useState(false);

  return (
    <div className="App">
        <div>
         <h2>Blog</h2>
        </div>
        <Card />
        {
          cardDetail === true
          ? <CardDetail />
          : null
        }
        <input></input><button>저장</button>

    </div>
  );
}

function Card() {
  return (
    <div className="card">
      <h3>글제목: </h3><span>좋아요 ♡</span>
      <p>발행일: </p>
    </div>
  )
}

function CardDetail() {
  return (
    <div className="card">
      <h3>글제목: </h3>
      <p>발행일: </p>
      <p>상세내용: </p>
    </div>
  )
}

export default App;
