import React , { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import Card from './card.js';
import CardDetail from './cardDetail.js';

function App() {
  const [cardDetail, onCardDetail] = useState(false);
  const [blogTitle, changeBlogTitle] = useState([]);
  const [cardList, addCardList] = useState([]);

  function addContents() {
    const newArray = [...cardList];
    newArray.push(blogTitle) ;
    addCardList( newArray );
    console.log(cardList)
  }

  return (
    <div className="App">
        <div className="Navbar">
         <h2>Blog</h2>
        </div>
        <Card />
        {
          cardDetail === true
          ? <CardDetail />
          : null
        }
        <input onChange = { (e) => changeBlogTitle(e.target.value) }></input><button onClick = { (e)=> addContents(e) } >저장</button>

    </div>
  );
}


export default App;
