import React , { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import Card from './card.js';
import CardDetail from './cardDetail.js';

function App() {
  const [blogTitle, changeBlogTitle] = useState({title: "", time: ""});
  const [cardList, addCardList] = useState([]);
  const [cardDetailUI, onCardDetailUI] = useState(false);

  function getTime() {
    const now = new Date();
    const year = now.getFullYear();
    const month = now.getMonth();
    const data = now.getDate();

    return  `${ year }년${ month }월${ data }일`
  }

  function addContents() {
    const newArray = [...cardList];
    newArray.unshift(blogTitle) ;
    addCardList( newArray );
  }

  return (
    <div className="App">
        <div className="Navbar">
         <h2>Blog</h2>
         <input onChange = { (e) => changeBlogTitle({title: e.target.value, time: getTime()}) }></input><button onClick = { (e)=> addContents(e) } >저장</button>
        </div>
        {
          cardList.map( (title)=>{
            return(
              <Card title={title} />
            )
          } )
        }
        {
          cardDetailUI === true
          ? <CardDetail />
          : null
        }

    </div>
  );
}


export default App;
