import React , { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [cardDetail, onCardDetail] = useState(false);
  const [blogTitle, changeBlogTitle] = useState([]);
  const [cardList, addCardList] = useState([]);

  function addContents(e) {
    const newArray = [...cardList];
    newArray.push(e.target.value) ;
    addCardList( newArray );
  }

  return (
    <div className="App">
        <div className="Navbar">
         <h2>Blog</h2>
        </div>
        <Card />
        {
          cardDetail === true
          ? <CardDetail />
          : null
        }
        <input></input><button onClick = { (e)=> addContents(e) } >저장</button>

    </div>
  );
}

function Card(props) {
  return (
    <div className="card">
      <h3>글제목: {  props.blogTitle } </h3><span>좋아요 ♡</span>
      <p>발행일: </p>
    </div>
  )
}

function CardDetail() {
  return (
    <div className="card">
      <h3>글제목: </h3>
      <p>발행일: </p>
      <p>상세내용: </p>
    </div>
  )
}

export default App;
